import { Component, OnInit } from '@angular/core';
import {SigninService } from '../../services/signin.service';
import { FcrService} from '../../services/fcr.service';
import { Campaign} from '../../model/campaign';
import * as jsPDF from 'jspdf'

@Component({
  selector: 'app-fcr-add-campaign',
  templateUrl: './fcr-add-campaign.component.html',
  styleUrls: ['./fcr-add-campaign.component.css']
})
export class FcrAddCampaignComponent implements OnInit {

  constructor(
    private signinService : SigninService,
    private fcrSercice : FcrService
  ){ }
             
 
  ngOnInit() {

    this.getLoggedInUser();
  
  }
  
  startDate : any ;
  endDate : any;
  taluka :String = '';
  hospitalList = [];
  campaignList = [];
  toggleForm : boolean = false;
  selectedCampaign : Campaign;

  onSubmit(form){
   
    const campaign : Campaign = {
      nameOfCampaign : form.value.nameOfCampaign,
      startDate : form.value.startDate,
      endDate : form.value.endDate,
      time : form.value.time,
      disease1:form.value.disease1,
      disease2:form.value.disease2,
      disease3 : form.value.disease3,
      location : form.value.location,
      description : form.value.description,
      showCampaign:false
    }

    this.fcrSercice.addCampaign(campaign)
    .subscribe(
      data => {
        this.getListOfCampaigns(this.taluka);
      },
      error => console.log(error)
    )

    form.resetForm();

  }

  getLoggedInUser(){

    this.signinService.getLoggedInUser()
    .subscribe(
      data => {
        this.taluka = data.user.taluka;
        this.getListOfHospitals(this.taluka);
        this.getListOfCampaigns(this.taluka);
      },
      error => console.log(error)
    )
  }

  getListOfHospitals(taluka){

    this.fcrSercice.getListOfHospitals(taluka)
    .subscribe(
      data => {
        this.hospitalList = data.hospitals;
      },
      error =>console.log(error)
    )

  }

  getListOfCampaigns(taluka){

    this.fcrSercice.getListOfCampaigns(taluka)
    .subscribe(
      data =>{
        this.campaignList = data.campaigns;
      },
      error => console.log(error)
    )
  }

  updateShowCampaign(campaign){

    campaign.showCampaign = !campaign.showCampaign;

    this.fcrSercice.updateShowCampaign(campaign)
    .subscribe(
      data => console.log(data),
      error => console.log(error)
    )

  }

  deleteCampaign(id){

    this.fcrSercice.deleteCampaign(id)
    .subscribe(
      data =>{
        if(data.result.n==1){
          for(let i=0;i<this.campaignList.length;i++){
            if(id == this.campaignList[i]._id){
              this.campaignList.splice(i,1);
            }
          }
      }
    },
    error => console.log(error)
    )

  }

  editCampaign(campaign){

    this.selectedCampaign = campaign;
    this.toggleForm = !this.toggleForm;
  }

  updateCampaign(form){

    const updatedCampaign : Campaign = {
      _id:this.selectedCampaign._id,
      nameOfCampaign : form.value.nameOfCampaign,
      startDate : form.value.startDate,
      endDate : form.value.endDate,
      time : form.value.time,
      disease1:form.value.disease1,
      disease2:form.value.disease2,
      disease3 : form.value.disease3,
      location : form.value.location,
      description : form.value.description,
      showCampaign:this.selectedCampaign.showCampaign
    }

    this.fcrSercice.updateCampaign(updatedCampaign)
    .subscribe(
     data=> {
        this.getListOfCampaigns(this.taluka);
      },
      error => console.log(error)
    )

    form.resetForm();

    this.toggleForm = ! this.toggleForm;

  }

  downloadPDF(campaign){

    var imgData ="data:image/png;base64,";

    let imgData1 = "data:image/png;base64,"
    var doc = new jsPDF()

    doc.addImage(imgData, 'JPEG',  15, 1, 40, 40)
    doc.addImage(imgData1, 'JPEG',  165, 1, 30, 30)
    doc.rect(5,35, 200, 180)
    doc.rect(5,215, 200, 40)
    doc.line(5,90,205,90)
    doc.text(75,10,' ** AyushmanOdisha **')
    doc.text(75,10,' ** AyushmanOdisha **')
    doc.text(75,20,'Government of Odisha')
    doc.line(5,55,205,55)
    doc.text(55,45,'Name of Campaign : ')
    doc.text(110,45,`${campaign.nameOfCampaign}`)
    doc.text(110,45,`${campaign.nameOfCampaign}`)
    doc.text(15,75,'Start Date :')
    doc.text(45,75,`${campaign.startDate.day}/${campaign.startDate.month}/${campaign.startDate.year}`)
    doc.text(45,75,`${campaign.startDate.day}/${campaign.startDate.month}/${campaign.startDate.year}`)
    doc.text(85,75,'End Date :')
    doc.text(115,75,`${campaign.endDate.day}/${campaign.endDate.month}/${campaign.endDate.year}`)
    doc.text(115,75,`${campaign.endDate.day}/${campaign.endDate.month}/${campaign.endDate.year}`)
    doc.text(155,75,'Time :')
    doc.text(175,75,`${campaign.time}`)
    doc.text(175,75,`${campaign.time}`)
    doc.text(15,105,'Disease :')
   if(campaign.disease1){
    doc.text(45,105,`${campaign.disease1}`)
   }
   if(campaign.disease2){
    doc.text(45,115,`${campaign.disease2}`)
   }
   if(campaign.disease3){
    doc.text(45,125,`${campaign.disease3}`)
   }
    doc.text(15,140,'Location :')
    doc.text(45,140,`${campaign.location}`)
    doc.text(45,140,`${campaign.location}`)
    doc.text(15,165,'Description:')
    doc.text(50,165,`${campaign.description}`)
    doc.text(10,230,'Note :')
    doc.text(10,230,'Note :')
    doc.text(28,230,'1 : This is your responsibility to provide this information to all villegers')
    doc.text(28 ,240,'2 : For more details feel free to reach us')
    doc.text(30,270,'** Healthy citizens are the greatest asset any country can have **')
    doc.text(30,270,'** Healthy citizens are the greatest asset any country can have **')

    doc.save(`${campaign.nameOfCampaign}.pdf`);

  }
  
 

 
  }
